import React, { useState, useEffect } from 'react';
import axios from 'axios';
const forge = require('node-forge');



const Search = ({ token }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResult, setSearchResult] = useState([]);
  const [userRole, setUserRole] = useState('');

  function decrypt3DES(input, key) {
    let md5Key = forge.md.md5.create();
    md5Key.update(key);
    md5Key = md5Key.digest().toHex();
    const decipher = forge.cipher.createDecipher('3DES-ECB', md5Key.substring(0, 24));
    decipher.start();
  
    const inputBytes = forge.util.decode64(input);
    decipher.update(forge.util.createBuffer(inputBytes));
    decipher.finish();
    const decrypted = decipher.output;
    return decrypted.toString("utf8");
  }

  const fetchData = async () => {
    try {
      const response = await axios.get(`http://localhost:3001/search?term=${searchTerm}`, {
        headers: {
          Authorization: token,
        },
      });

      const decryptedData = response.data.map((customer) => {
        // Decrypt credit card and medical records
        const decryptedCreditCard = decrypt3DES(customer.creditCard, "A5dghfghA5dghfghA5dghfghA5dghfgh");
        const decryptedMedicalRecords = decrypt3DES(customer.medicalRecords, "A5dghfghA5dghfghA5dghfghA5dghfgh");

        // Return the decrypted data along with other customer details
        return {
          ...customer,
          decryptedCreditCard,
          decryptedMedicalRecords,
        };
      });

      setSearchResult(decryptedData);
    } catch (error) {
      console.error('Search failed', error);
    }
  };

  
  const handleSearch = () => {
    // Trigger search when the button is clicked
    fetchData();
  };

  useEffect(() => {
    const tokenPayload = token.split('.')[1];
    const decodedPayload = JSON.parse(atob(tokenPayload));
    const role = decodedPayload.role;
    setUserRole(role);
  }, [token]);

  const displayCustomerDetails = (customer) => {
    const decryptedCreditCard = decrypt3DES(customer.creditCard, "A5dghfghA5dghfghA5dghfghA5dghfgh");
    const decryptedMedicalRecords = decrypt3DES(customer.medicalRecords, "A5dghfghA5dghfghA5dghfghA5dghfgh");
    return (
      <div key={customer.id}>
        <p>Name: {customer.name}</p>
        <p>Email: {customer.email}</p>
        <p>Address: {customer.address}</p>
        <p>Phone: {customer.phone}</p>
        {userRole === 'admin' ? (
          <>
            <p>Credit Card: {decryptedCreditCard}</p>
            <p>Medical Records: {customer.medicalRecords}</p>
          </>
        ) : userRole === 'privileged' ? (
          <p>Credit Card: {customer.creditCard}</p>
        ) : (
          // For normal users
          null
        )}
        <hr />
      </div>
    );
  };

  return (
    <div>
      <h2>Search</h2>
      <input
        type="text"
        placeholder="Enter search term..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>
      {searchResult.map((customer) => displayCustomerDetails(customer))}
    </div>
  );
};

export default Search;
