import React, { useState, useEffect } from 'react';
import axios from 'axios';
import forge from 'node-forge';

const Search = ({ token }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResult, setSearchResult] = useState([]);
  const [userRole, setUserRole] = useState('');

  const decryptData = (data, iv, key) => {
    try {
      // Convert the hex-encoded data and IV to buffers
      const encryptedBuffer = forge.util.createBuffer(forge.util.hexToBytes(data));
      const ivBuffer = forge.util.createBuffer(forge.util.hexToBytes(iv));
  
      // Convert the hex-encoded key to a buffer
      const keyBuffer = forge.util.createBuffer(forge.util.hexToBytes(key));
  
      // Create a decipher object using AES-CBC algorithm
      const decipher = forge.cipher.createDecipher('AES-CBC', keyBuffer);
  
      // Specify the IV
      decipher.start({ iv: ivBuffer });
  
      // Update the decipher with the encrypted data
      decipher.update(encryptedBuffer);
  
      // Finalize the deciphering process
      decipher.finish();
  
      // Get the output (decrypted text)
      const decryptedText = decipher.output.toString();
  
      return decryptedText;
    } catch (error) {
      console.error('Decryption failed:', error.message);
      return null;
    }
  };
  
  
  

  const fetchData = async () => {
    try {
      const response = await axios.get(`http://localhost:3001/search?term=${searchTerm}`, {
        headers: {
          Authorization: token,
        },
      });

      const decryptedData = response.data.map((customer) => {
        // Decrypt credit card and medical records
        const decryptedCreditCard = decryptData(customer.creditCard, customer.ivCreditCard, "hghghghs12342345hghghghs12342345");
        const decryptedMedicalRecords = decryptData(customer.medicalRecords, customer.ivMedicalRecords, "hghghghs12342345hghghghs12342345");

        // Return the decrypted data along with other customer details
        return {
          ...customer,
          decryptedCreditCard,
          decryptedMedicalRecords,
        };
      });

      setSearchResult(decryptedData);
    } catch (error) {
      console.error('Search failed', error);
    }
  };

  const handleSearch = () => {
    // Trigger search when the button is clicked
    fetchData();
  };

  useEffect(() => {
    const tokenPayload = token.split('.')[1];
    const decodedPayload = JSON.parse(atob(tokenPayload));
    const role = decodedPayload.role;
    setUserRole(role);
  }, [token]);

  const displayCustomerDetails = (customer) => {
    try {
      const key = 'hghghghs12342345hghghghs12342345';
      const decryptedCreditCard = decryptData(customer.creditCard, customer.ivCreditCard, key);
      const decryptedMedicalRecords = decryptData(customer.medicalRecords, customer.ivMedicalRecords, key);
  
      const encodedCreditCard = encodeURIComponent(decryptedCreditCard);
      const encodedMedicalRecords = encodeURIComponent(decryptedMedicalRecords);
  
      return (
        <div key={customer.id}>
          <p>Name: {customer.name}</p>
          <p>Email: {customer.email}</p>
          <p>Address: {customer.address}</p>
          <p>Phone: {customer.phone}</p>
          {userRole === 'admin' ? (
            <>
              <p>Credit Card: {encodedCreditCard}</p>
              <p>Medical Records: {encodedMedicalRecords}</p>
            </>
          ) : userRole === 'privileged' ? (
            <p>Credit Card: {encodedCreditCard}</p>
          ) : (
            // For normal users
            null
          )}
          <hr />
        </div>
      );
    } catch (error) {
      console.error('Display customer details failed:', error.message);
      return null;
    }
  };
  
  return (
    <div>
      <h2>Search</h2>
      <input
        type="text"
        placeholder="Enter search term..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>
      {searchResult.map((customer) => displayCustomerDetails(customer))}
    </div>
  );
};

export default Search;
