import React, { useState, useEffect } from 'react';
import axios from 'axios';
import forge from 'node-forge';

const Search = ({ token }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResult, setSearchResult] = useState([]);
  const [userRole, setUserRole] = useState('');

  const decryptData = (encryptedData, iv, key) => {
    try {
      // Convert hex data to bytes
      encryptedData = forge.util.hexToBytes(encryptedData);
      iv = forge.util.createBuffer(iv).getBytes();
  
      const decipher = forge.cipher.createDecipher('AES-CBC', key);
      decipher.start({ iv });
      decipher.update(forge.util.createBuffer(encryptedData));
      decipher.finish();
  
      // Convert the decrypted data to a UTF-8 string
      const decryptedString = decipher.output.toString('utf8');
  
      // Decode the URI component
      const decodedString = decodeURIComponent(decryptedString);
  
      return decodedString;
    } catch (error) {
      console.error('Decryption failed:', error.message);
      return null;
    }
  };
  
  
  

  const fetchData = async () => {
    try {
      const response = await axios.get(`http://localhost:3001/search?term=${searchTerm}`, {
        headers: {
          Authorization: token,
        },
      });

      const decryptedData = response.data.map((customer) => {
        // Decrypt credit card and medical records
        const decryptedCreditCard = decryptData(customer.creditCard, customer.ivCreditCard);
        const decryptedMedicalRecords = decryptData(customer.medicalRecords, customer.ivMedicalRecords);

        // Return the decrypted data along with other customer details
        return {
          ...customer,
          decryptedCreditCard,
          decryptedMedicalRecords,
        };
      });

      setSearchResult(decryptedData);
    } catch (error) {
      console.error('Search failed', error);
    }
  };

  const handleSearch = () => {
    // Trigger search when the button is clicked
    fetchData();
  };

  useEffect(() => {
    const tokenPayload = token.split('.')[1];
    const decodedPayload = JSON.parse(atob(tokenPayload));
    const role = decodedPayload.role;
    setUserRole(role);
  }, [token]);

  const displayCustomerDetails = (customer) => {
    return (
      <div key={customer.id}>
        <p>Name: {customer.name}</p>
        <p>Email: {customer.email}</p>
        <p>Address: {customer.address}</p>
        <p>Phone: {customer.phone}</p>
        {userRole === 'admin' ? (
          <>
            <p>Credit Card: {customer.decryptedCreditCard}</p>
            <p>Medical Records: {customer.decryptedMedicalRecords}</p>
          </>
        ) : userRole === 'privileged' ? (
          <p>Credit Card: {customer.decryptedCreditCard}</p>
        ) : (
          // For normal users
          null
        )}
        <hr />
      </div>
    );
  };

  return (
    <div>
      <h2>Search</h2>
      <input
        type="text"
        placeholder="Enter search term..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>
      {searchResult.map((customer) => displayCustomerDetails(customer))}
    </div>
  );
};

export default Search;
