import React, { useState, useEffect } from 'react';
import axios from 'axios';
import forge from 'node-forge'; // Make sure to install 'node-forge' using npm or yarn

const Search = ({ token }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResult, setSearchResult] = useState([]);
  const [userRole, setUserRole] = useState('');

  const decryptData = (encryptedText, iv) => {
    const decipher = forge.cipher.createDecipher('AES-CBC', forge.util.hexToBytes('your-secret-key-here'));
    decipher.start({ iv: forge.util.hexToBytes(iv) });
    decipher.update(forge.util.createBuffer(forge.util.hexToBytes(encryptedText)));
    const result = decipher.finish(); // check 'result' for true/false
    if (result) {
      return decipher.output.data;
    } else {
      console.error('Decryption failed');
      return null;
    }
  };

  const fetchData = async () => {
    try {
      const response = await axios.get(`http://localhost:3001/search?term=${searchTerm}`, {
        headers: {
          Authorization: token,
        },
      });

      setSearchResult(response.data);
    } catch (error) {
      console.error('Search failed', error);
    }
  };

  const handleSearch = () => {
    fetchData();
  };

  useEffect(() => {
    const tokenPayload = token.split('.')[1];
    const decodedPayload = JSON.parse(atob(tokenPayload));
    const role = decodedPayload.role;
    setUserRole(role);
  }, [token]);

  const displayCustomerDetails = (customer) => {
    const { creditCard, medicalRecords } = customer;

    // Decrypt Credit Card
    const decryptedCreditCard = decryptData(creditCard, customer.iv);

    return (
      <div key={customer.id}>
        <p>Name: {customer.name}</p>
        <p>Email: {customer.email}</p>
        <p>Address: {customer.address}</p>
        <p>Phone: {customer.phone}</p>
        {userRole === 'admin' ? (
          <>
            <p>Credit Card: {decryptedCreditCard}</p>
            <p>Medical Records: {medicalRecords}</p>
          </>
        ) : userRole === 'privileged' ? (
          <p>Credit Card: {decryptedCreditCard}</p>
        ) : (
          // For normal users
          null
        )}
        <hr />
      </div>
    );
  };

  return (
    <div>
      <h2>Search</h2>
      <input
        type="text"
        placeholder="Enter search term..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>
      {searchResult.map((customer) => displayCustomerDetails(customer))}
    </div>
  );
};

export default Search;
