import React, { useState, useEffect } from 'react';
import axios from 'axios';
import forge from 'node-forge'; // Make sure to install the node-forge library

const Search = ({ token }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResult, setSearchResult] = useState([]);
  const [userRole, setUserRole] = useState('');

  const decryptData = (encryptedData, iv) => {
    try {
      const decipher = forge.cipher.createDecipher('AES-CBC', forge.util.hexToBytes(iv));
      decipher.start({ iv: forge.util.hexToBytes(iv) });
      decipher.update(forge.util.createBuffer(forge.util.hexToBytes(encryptedData)));
      const result = decipher.finish(); // Check if decryption was successful
  
      if (result) {
        return decipher.output.toString();
      } else {
        console.error('Decryption failed: Unable to finish decryption');
        return null;
      }
    } catch (error) {
      console.error('Decryption failed:', error.message);
      return null;
    }
  };
  

  const fetchData = async () => {
    try {
      const response = await axios.get(`http://localhost:3001/search?term=${searchTerm}`, {
        headers: {
          Authorization: token,
        },
      });

      // Decrypt medical records and credit card for each customer
      const decryptedData = response.data.map((customer) => ({
        ...customer,
        creditCard: decryptData(customer.creditCard, customer.ivCreditCard),
        medicalRecords: decryptData(customer.medicalRecords, customer.ivMedicalRecords),
      }));

      setSearchResult(decryptedData);
    } catch (error) {
      console.error('Search failed', error);
    }
  };

  const handleSearch = () => {
    // Trigger search when the button is clicked
    fetchData();
  };

  useEffect(() => {
    const tokenPayload = token.split('.')[1];
    const decodedPayload = JSON.parse(atob(tokenPayload));
    const role = decodedPayload.role;
    setUserRole(role);
  }, [token]);

  const displayCustomerDetails = (customer) => {
    return (
      <div key={customer.id}>
        <p>Name: {customer.name}</p>
        <p>Email: {customer.email}</p>
        <p>Address: {customer.address}</p>
        <p>Phone: {customer.phone}</p>
        {userRole === 'admin' ? (
          <>
            <p>Credit Card: {customer.creditCard}</p>
            <p>Medical Records: {customer.medicalRecords}</p>
          </>
        ) : userRole === 'privileged' ? (
          <p>Credit Card: {customer.creditCard}</p>
        ) : (
          // For normal users
          null
        )}
        <hr />
      </div>
    );
  };

  return (
    <div>
      <h2>Search</h2>
      <input
        type="text"
        placeholder="Enter search term..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>
      {searchResult.map((customer) => displayCustomerDetails(customer))}
    </div>
  );
};

export default Search;
