import React, { useState } from 'react';
import axios from 'axios';
import jwt_decode from 'jwt-decode';

const AddCustomer = ({ token }) => {
  const [customerData, setCustomerData] = useState({
    name: '',
    email: '',
    address: '',
    phone: '',
    creditCard: '',
    medicalRecords: '',
  });

  const handleAddCustomer = async () => {
    try {
      const decodedToken = jwt_decode(token);

      // Check if the user has the 'admin' or 'privileged' role to add a customer
      if (decodedToken.role !== 'admin' && decodedToken.role !== 'privileged') {
        console.error('User does not have the required role to add a customer');
        return;
      }

      // Add customer logic
      await axios.post('http://localhost:3001/add-customer', customerData, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      console.log('Customer added successfully');
    } catch (error) {
      console.error('Error adding customer', error);
    }
  };

  return (
    <div>
      <h2>Add Customer</h2>
      <input
        type="text"
        placeholder="Name"
        onChange={(e) => setCustomerData({ ...customerData, name: e.target.value })}
      />
      <input
        type="text"
        placeholder="Email"
        onChange={(e) => setCustomerData({ ...customerData, email: e.target.value })}
      />
      <input
        type="text"
        placeholder="Address"
        onChange={(e) => setCustomerData({ ...customerData, address: e.target.value })}
      />
      <input
        type="text"
        placeholder="Phone"
        onChange={(e) => setCustomerData({ ...customerData, phone: e.target.value })}
      />
      <input
        type="text"
        placeholder="Credit Card"
        onChange={(e) => setCustomerData({ ...customerData, creditCard: e.target.value })}
      />
      <input
        type="text"
        placeholder="Medical Records"
        onChange={(e) => setCustomerData({ ...customerData, medicalRecords: e.target.value })}
      />
      <button onClick={handleAddCustomer}>Add Customer</button>
    </div>
  );
};

export default AddCustomer;
