import React, { useState, useEffect } from 'react';
import axios from 'axios';
import forge from 'node-forge';

const Search = ({ token }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResult, setSearchResult] = useState([]);
  const [userRole, setUserRole] = useState('');

  const crypto = require('crypto');

  const decryptData = (data, iv, key) => {
    try {
      const decipher = crypto.createDecipheriv('aes-256-cbc', Buffer.from(key), Buffer.from(iv, 'hex'));
      let decrypted = decipher.update(data, 'hex', 'utf-8');
      decrypted += decipher.final('utf-8');
  
      // Handle URI decoding if needed
      const decodedText = decodeURIComponent(decrypted);
  
      return decodedText;
    } catch (error) {
      console.error('Decryption failed:', error.message);
      return null;
    }
  };
  
  
  
  
  

  const fetchData = async () => {
    try {
      const response = await axios.get(`http://localhost:3001/search?term=${searchTerm}`, {
        headers: {
          Authorization: token,
        },
      });

      const decryptedData = response.data.map((customer) => {
        // Decrypt credit card and medical records
        const decryptedCreditCard = decryptData(customer.creditCard, customer.ivCreditCard, "hghghghs12342345hghghghs12342345");
        const decryptedMedicalRecords = decryptData(customer.medicalRecords, customer.ivMedicalRecords, "hghghghs12342345hghghghs12342345");

        // Return the decrypted data along with other customer details
        return {
          ...customer,
          decryptedCreditCard,
          decryptedMedicalRecords,
        };
      });

      setSearchResult(decryptedData);
    } catch (error) {
      console.error('Search failed', error);
    }
  };

  const handleSearch = () => {
    // Trigger search when the button is clicked
    fetchData();
  };

  useEffect(() => {
    const tokenPayload = token.split('.')[1];
    const decodedPayload = JSON.parse(atob(tokenPayload));
    const role = decodedPayload.role;
    setUserRole(role);
  }, [token]);

  const displayCustomerDetails = (customer) => {
    try {
      const key = 'hghghghs12342345hghghghs12342345';
      
      // Decode and then decrypt
      const decodedCreditCard = decodeURIComponent(customer.creditCard);
      const decodedMedicalRecords = decodeURIComponent(customer.medicalRecords);
      
      const decryptedCreditCard = decryptData(decodedCreditCard, customer.ivCreditCard, key);
      const decryptedMedicalRecords = decryptData(decodedMedicalRecords, customer.ivMedicalRecords, key);
  
      return (
        <div key={customer.id}>
          <p>Name: {customer.name}</p>
          <p>Email: {customer.email}</p>
          <p>Address: {customer.address}</p>
          <p>Phone: {customer.phone}</p>
          {userRole === 'admin' ? (
            <>
              <p>Credit Card: {decryptedCreditCard}</p>
              <p>Medical Records: {decryptedMedicalRecords}</p>
            </>
          ) : userRole === 'privileged' ? (
            <p>Credit Card: {decryptedCreditCard}</p>
          ) : (
            // For normal users
            null
          )}
          <hr />
        </div>
      );
    } catch (error) {
      console.error('Display customer details failed:', error.message);
      return null;
    }
  };
  
  
  return (
    <div>
      <h2>Search</h2>
      <input
        type="text"
        placeholder="Enter search term..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>
      {searchResult.map((customer) => displayCustomerDetails(customer))}
    </div>
  );
};

export default Search;
