import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Search = ({ token }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResult, setSearchResult] = useState([]);
  const [userRole, setUserRole] = useState('');

  const fetchData = async () => {
    try {
      const decodedToken = decodeToken(token);
      const role = decodedToken.role;
      setUserRole(role);

      let endpoint = 'search';

      if (searchTerm === '*') {
        endpoint = 'search-all';
      }

      const response = await axios.get(`http://localhost:3001/${endpoint}?term=${searchTerm}`, {
        headers: {
          Authorization: token,
        },
      });

      setSearchResult(response.data);
    } catch (error) {
      console.error('Search failed', error);
    }
  };

  useEffect(() => {
    fetchData();
  }, [token, searchTerm]);

  const decodeToken = (token) => {
    const tokenPayload = token.split('.')[1];
    return JSON.parse(atob(tokenPayload));
  };

  const handleSearch = () => {
    // Trigger search when the button is clicked
    fetchData();
  };

  const displayCustomerDetails = (customer) => {
    return (
      <div key={customer.id}>
        <p>Name: {customer.name}</p>
        <p>Email: {customer.email}</p>
        <p>Address: {customer.address}</p>
        <p>Phone: {customer.phone}</p>
        {userRole === 'admin' ? (
          <>
            <p>Credit Card: {customer.creditCard}</p>
            <p>Medical Records: {customer.medicalRecords}</p>
          </>
        ) : userRole === 'privileged' ? (
          <p>Credit Card: {customer.creditCard}</p>
        ) : (
          // For normal users
          null
        )}
        <hr />
      </div>
    );
  };

  return (
    <div>
      <h2>Search</h2>
      <input
        type="text"
        placeholder="Enter search term"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>
      {searchResult.map((customer) => displayCustomerDetails(customer))}
    </div>
  );
};

export default Search;
